package com.interswitch.volcano.Unsolo.configurations;import lombok.RequiredArgsConstructor;import org.modelmapper.ModelMapper;import org.springframework.context.annotation.Bean;import org.springframework.context.annotation.Configuration;@Configuration@RequiredArgsConstructorpublic class ModelMapperConfig {    @Bean    public ModelMapper getModelMapper(){        ModelMapper modelMapper = new ModelMapper();        modelMapper.getConfiguration().setSkipNullEnabled(true);        return modelMapper;    }}